import {useState,useEffect} from 'react';

import API_KEY from './pages/keys';


const CONTEXT_KEY='60b924a5d0c241f2a';

const useGoogleSearch=(term)=>{

	const[data,setData]=useEffect(null);

	useEffect(()=>{

		const fetchData=async()=>{

			fetch(`https://www.googleapis.com/customsearch/v1?key=${API_KEY} &cx=${CONTEXT_KEY} &q=${term}`).then(
				response=>(response.json()).then(result=>{
					setData(result)
				})
			)

		}

	},[term])

	return {data};

	fetchData();

}

//return a data have access to if and display it;

export default useGoogleSearch;
